import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from 'axios';

export default function Company(){
    const location=useLocation();
    const user=location.state && location.state.user;
    const[data,setdata]=useState([]);
    useEffect(
        ()=>{getdata();},[]
    )
    const getdata=(event)=>{
        axios.get('http://localhost:9090/getcompanybyuser').then(response=>{
            setdata(response.data);
            console.log(response.data);
        })
    }

    const delete1=(event,abc)=>{
        event.preventDefault()
        axios.get('http://localhost:9090/deletejob',abc).then(response=>{
            console.log(response.data);
        })
    }

    return(
        <div>
            {user?(
                <div>
                    <table>
                        <thead>
                        <tr>
                            <th>CompanyName</th>
                            <th>JobName</th>
                            <th>JobDesc</th>
                            <th>JobLocation</th>
                            <th>JobSalary</th>
                            <th>JobType</th>
                        </tr>
                        </thead>
                        <tbody>
                        {data.map((job,key)=>{
                            return(
                                <tr key={key}>
                                    <td>{job.companyname}</td>
                                    <td>{job.jobname}</td>
                                    <td>{job.jobdesc}</td>
                                    <td>{job.jobloc}</td>
                                    <td>{job.jobsal}</td>
                                    <td>{job.jobtype}</td>
                                    <td><button onClickCapture={delete1(job.jobdesc)}>Delete</button></td>
                                </tr>
                            )
                        })}
                        </tbody>
                    </table>
                </div>
            ):(<div>Please Login to View Data</div>)
            }
        </div>
    );
}
