import React, { useState } from "react";
import {Link} from 'react-router-dom';
import axios from 'axios';
export default function Register() {
    const [user,setuser]=useState("");
    const [pass,setpass]=useState("");
    const formData = new FormData();
    formData.append('username', user);
    formData.append('password', pass);

    const SaveVal=()=>
    {
        axios.post("http://localhost:9090/reg",formData).then(response => {
            const responseData = response.data.message;
            window.alert(responseData);
          });
    }
    return (
        <div>
        <h1>Hello User this is Register</h1>
        <form onSubmit={SaveVal}>
            <input type="text" placeholder="Enter Username" value={user} name="user" required onChange={(e)=>{setuser(e.target.value)}}></input>
            <input type="password" placeholder="Enter Password" value={pass} name="pass" required onChange={(e)=>{setpass(e.target.value)}}></input>
            <button type="submit">Register</button>
        </form>
        <Link to="/login">Login</Link>
        </div>
    );
}
