import React, { useState } from "react";
import {Link} from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './bootstrap.css';
export default function Login() {
    const [user,setuser]=useState("");
    const [pass,setpass]=useState("");
    const navigate = useNavigate();
    const formData = new FormData();
    formData.append('username', user);
    formData.append('password', pass);

    const SaveVal=(event)=>
    {
        event.preventDefault();
        axios.post("http://localhost:9090/log",formData).then(response => {
            const responseData = response.data;
            if(responseData==="Invalid")
            window.alert(responseData)
            else
            {
                //window.location.replace('http://localhost:3000')
                localStorage.setItem('token-info',JSON.stringify(user));
                navigate('/', { state: {user:user} });
            }
        });
    }
    return (
        <div class="form-group">
        <div>
        <h1>Hello User this is Login</h1>
        <form onSubmit={SaveVal}>
            Username : <input type="text" placeholder="Enter Username" value={user} name="user" required onChange={(e)=>{setuser(e.target.value)}}></input>
            Password : <input type="password" placeholder="Enter Password" value={pass} name="pass" required onChange={(e)=>{setpass(e.target.value)}}></input>
            <button type="submit">Login</button>
        </form>
        <Link to="/register">Register</Link>
        </div>
        </div>
    );
}
