import React from "react";
import { useLocation } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import {Link} from 'react-router-dom';
export default function Home1() {
        const location = useLocation();
        const navigate= useNavigate();
        const user = location.state && location.state.user;
        const logout=()=>{
            localStorage.removeItem('token-info');
            window.location.replace('http://localhost:3000/comlogin');
        }

        const job=()=>{
            navigate('/jobprofile',{ state: {user:user} });
        }

        const viewjob=()=>{
            navigate('/viewjob',{state: {user:user}});
        }

        const applystudent=()=>{
            navigate('/applystudent',{state: {user:user}});
        }

        return (
            <div>
            <h1>Hello {user}</h1><br/>
            {user?(<div><button onClickCapture={logout}>Logout </button> <button onClickCapture={job}>Register Job Profile</button> <button onClickCapture={viewjob}>Job Profiles Registered</button> <button onClickCapture={applystudent}></button></div>):(<div><h1>Please Login to view options </h1><Link to='/comlogin'>Login</Link></div>)}
            </div>
        );
}
