import React from "react";
import { useLocation } from 'react-router-dom';
import {Link} from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import './bootstrap.css';
export default function Home() {
        const location = useLocation();
        const navigate= useNavigate();
        const user = location.state && location.state.user;
        //const u1=JSON.parse(localStorage.getItem('token-info'));
        const logout=()=>{
            localStorage.removeItem('token-info');
            window.location.replace('http://localhost:3000/login');
        }
        const upload=()=>{
            navigate('/upload',{ state: {user:user} });
        }

        const applyjob=()=>{
            navigate('/applyjob',{state: {user:user}});
        }

        const download=(event)=>{
            axios.get("http://localhost:9090/getdata", { responseType: 'arraybuffer' }).then((response) => {
      const filename = user + ' resume.jpeg';
      const blob = new Blob([response.data], { type: 'application/jpeg' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = filename;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    });
        }
        const company=()=>{
            navigate('/company',{state:{user:user}});
        }
        return (
            <div>
            <h1>Hello {user}</h1><br/>
            {user?(<div><button type="button" class="btn btn-primary" onClickCapture={logout}>Logout </button><button type="button" class="btn btn-primary" onClickCapture={upload}>Upload</button><button type="button" class="btn btn-primary" onClickCapture={download}>Download</button><button type="button" class="btn btn-primary" onClickCapture={company}>Company List</button> <button type="button" class="btn btn-primary" onClickCapture={applyjob}>Applied Job Profiles</button></div>):(<div><h1>Please Login to view options </h1><Link to='/login'>Login</Link></div>)}
            </div>
        );
}
