import React, { useState,useEffect } from "react";
import axios from 'axios';
import Header from "./Header";
import './bootstrap.css';

export default function Profile(){
    const[data,setdata]=useState();
    const[abc,setabc]=useState(false);
    const user=JSON.parse(localStorage.getItem('token-info'));
    useEffect(
        ()=>{getdata();},[]
    )
    const getdata=(event)=>{
        if(user){
        axios.get('http://localhost:9090/userprofile/'+user).then(response=>{
            setdata(response.data);
            console.log(response.data);
        })
        }
    }
    return(
        <div>
        <Header></Header>
        {
            user?(
                <div>
                <center><h1>Profile</h1></center>
            <h2>Username : {data.username}</h2>
            <h2>Email : {data.email}</h2>
            <h2>College : {data.college}</h2>
            <h2>PhoneNo : {data.phoneno}</h2>
            <button type="button" className="btn-close" data-bs-dismiss="alert" onClickCapture={()=>{setabc(true)}}>Edit&#9998;</button>
        </div>
            ):(<h2>Please Login to View Data</h2>)
        }
        </div>
    );
}
