import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from 'axios';
import './bootstrap.css';

export default function Studentsapply(){
    const location=useLocation();
    const user=location.state && location.state.user;
    const[data,setdata]=useState([]);
    useEffect(
        ()=>{getdata();},[]
    )
    const getdata=(event)=>{
        axios.get('http://localhost:9090/getstudents').then(response=>{
            setdata(response.data);
            console.log(response.data);
        })
    }
    return(
        <div>
            {user?(
                <div>
                    <table>
                        <thead>
                        <tr>
                            <th>JobName</th>
                            <th>JobDesc</th>
                            <th>Student</th>
                            <th>Email</th>
                            <th>College</th>
                            <th>PhoneNo</th>
                            <th>Status</th>
                            <th>Resume</th>
                        </tr>
                        </thead>
                        <tbody>
                        {data.map((job,key)=>{
                            return(
                                <tr key={key}>
                                    <td>{job.jobname}</td>
                                    <td>{job.jobdesc}</td>
                                    <td>{job.username}</td>
                                    <td>{job.email}</td>
                                    <td>{job.college}</td>
                                    <td>{job.phoneno}</td>
                                    <td>{job.status}</td>
                                    <td><button onClickCapture={()=>{axios.get("http://localhost:9090/getresumedata/"+job.username, { responseType: 'arraybuffer' }).then((response) => {
      const filename = job.username + ' resume.jpeg';
      const blob = new Blob([response.data], { type: 'application/jpeg' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = filename;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
        })}}>Download</button></td>
        <td><button onClickCapture={()=>{axios.get("http://localhost:9090/rejected/"+job.jobdesc+"/"+job.username).then((response) => {
            window.alert(response.data);
        })}}>Remove</button></td>
                                </tr>
                            )
                        })}
                        </tbody>
                    </table>
                </div>
            ):(<div>Please Login to View Data</div>)
            }
        </div>
    );
}
