import React, { useState } from "react";
import {Link} from 'react-router-dom';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
export default function Login() {
    const [user,setuser]=useState("");
    const [pass,setpass]=useState("");
    const history = useHistory();
    const formData = new FormData();
    formData.append('username', user);
    formData.append('password', pass);

    const SaveVal=(event)=>
    {
        event.preventDefault();
        axios.post("http://localhost:9090/log",formData).then(response => {
            const responseData = response.data;
            if(responseData==="Invalid")
            window.alert(responseData)
            else
            //window.location.replace('http://localhost:3000')
            history.push({
                pathname: '/',
                state: { user: responseData }
              });
        });
    }
    return (
        <div>
        <h1>Hello User this is Login</h1>
        <form onSubmit={SaveVal}>
            <input type="text" placeholder="Enter Username" value={user} name="user" required onChange={(e)=>{setuser(e.target.value)}}></input>
            <input type="password" placeholder="Enter Password" value={pass} name="pass" required onChange={(e)=>{setpass(e.target.value)}}></input>
            <button type="submit">Login</button>
        </form>
        <Link to="/register">Register</Link>
        </div>
    );
}
