import React from "react";
import { useLocation } from 'react-router-dom';
import {Link} from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
export default function Home() {
        const location = useLocation();
        const navigate= useNavigate();
        const user = location.state && location.state.user;
        const logout=()=>{
            localStorage.removeItem('token-info');
            window.location.replace('http://localhost:3000/login');
        }
        const upload=()=>{
            navigate('/upload',{ state: {user:user} });
        }
        const download=(event)=>{
            event.preventDefault();
            axios.get("http://localhost:9090/getdata", { responseType: 'arraybuffer' }).then((response) => {
      const filename = user + ' resume.pdf';
      const blob = new Blob([response.data], { type: 'application/octet-stream' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = filename;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    });
        }
        return (
            <div>
            <h1>Hello {user}</h1><br/>
            {user?(<div><button onClickCapture={logout}>Logout </button><button onClickCapture={upload}>Upload</button><button onClickCapture={download}>Download</button></div>):(<div><h1>Please Login to view options </h1><Link to='/login'>Login</Link></div>)}
            </div>
        );
}
