import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from 'axios';
import './bootstrap.css';

export default function Applyjob(){
    const location=useLocation();
    const user=location.state && location.state.user;
    const formData = new FormData();
    const[data,setdata]=useState([]);
    useEffect(
        ()=>{getdata();},[]
    )
    const getdata=(event)=>{
        axios.get('http://localhost:9090/getapplyjob').then(response=>{
            setdata(response.data);
            console.log(response.data);
        })
    }
    const removeapply=()=>{
        formData.append('username',job.username);
        formData.append('companyname',job.companyname);
        formData.append('jobname',job.jobname);
        formData.append('jobdesc',job.jobdesc);
        formData.append('status',job.status);
        axios.delete("http://localhost:9090/removeapply",formData).then((response) => {
window.alert(response.data);
})}
    return(
        <div>
            {user?(
                <div>
                    <table>
                        <thead>
                        <tr>
                            <th>Username</th>
                            <th>CompanyName</th>
                            <th>JobName</th>
                            <th>JobDesc</th>
                            <th>Status</th>
                        </tr>
                        </thead>
                        <tbody>
                        {data.map((job,key)=>{
                            if(job.status!=="Rejected")
                            {
                            return(
                                <tr key={key}>
                                    <td>{job.username}</td>
                                    <td>{job.companyname}</td>
                                    <td>{job.jobname}</td>
                                    <td>{job.jobdesc}</td>
                                    <td>{job.status}</td>
                                    <td>
                                        <button onClickCapture={removeapply}>Remove</button></td>
                                </tr>
                            )}
                            else{
                                return(
                                    <tr key={key}>
                                        <td>{job.username}</td>
                                        <td>{job.companyname}</td>
                                        <td>{job.jobname}</td>
                                        <td>{job.jobdesc}</td>
                                        <td>{job.status}</td>
                                    </tr>
                                )
                            }
                        })}
                        </tbody>
                    </table>
                </div>
            ):(<div>Please Login to View Data</div>)
            }
        </div>
    );
}
