import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from 'axios';
import './bootstrap.css';

export default function Company(){
    const location=useLocation();
    const user=location.state && location.state.user;
    const[data,setdata]=useState([]);
    const formData = new FormData();
    useEffect(
        ()=>{getdata();},[]
    )
    const getdata=(event)=>{
        axios.get('http://localhost:9090/getcompany').then(response=>{
            setdata(response.data);
            console.log(response.data);
        })
    }
    return(
        <div>
            {user?(
                <div>
                    <table class="table table-hover">
                        <thead>
                        <tr>
                            <th scope="col">CompanyName</th>
                            <th scope="col">JobName</th>
                            <th >JobDesc</th>
                            <th>JobLocation</th>
                            <th>JobSalary</th>
                            <th>JobType</th>
                        </tr>
                        </thead>
                        <tbody>
                        {data.map((job,key)=>{
                            return(
                                <tr key={key}>
                                    <td>{job.companyname}</td>
                                    <td>{job.jobname}</td>
                                    <td>{job.jobdesc}</td>
                                    <td>{job.jobloc}</td>
                                    <td>{job.jobsal}</td>
                                    <td>{job.jobtype}</td>
                                    <td><button onClickCapture={(event)=>{event.preventDefault();
                                    formData.append('companyname',job.companyname);
                                    formData.append('jobdesc',job.jobdesc);
                                    formData.append('jobname',job.jobname);
                                    formData.append('username', user);
                                    axios.post('http://localhost:9090/applyjob',formData).then(response=>{
            window.alert(response.data);
        })}}>Apply</button></td>
                                </tr>
                            )
                        })}
                        </tbody>
                    </table>
                </div>
            ):(<div>Please Login to View Data</div>)
            }
        </div>
    );
}
