import React, { useState } from "react";
import {Link} from 'react-router-dom';
import axios from 'axios';
export default function Register() {
    const [user,setuser]=useState("");
    const [pass,setpass]=useState("");
    const formData = new FormData();
    formData.append('username', user);
    formData.append('password', pass);

    const SaveVal=()=>
    {
        const res= axios.post("http://localhost:9090/reg",formData);
        res.then(response => {
            // This code will run when the promise resolves (i.e., when the request is complete)
            const responseData = response.data;
            window.alert(responseData); // Display the data in a window alert
          })
          .catch(error => {
            // Handle errors here
            console.error(error);
          });
    }
    return (
        <div>
        <h1>Hello User this is Register</h1>
        <form onSubmit={SaveVal}>
            <input type="text" placeholder="Enter Username" value={user} name="user" required onChange={(e)=>{setuser(e.target.value)}}></input>
            <input type="password" placeholder="Enter Password" value={pass} name="pass" required onChange={(e)=>{setpass(e.target.value)}}></input>
            <button type="submit">Register</button>
        </form>
        <Link to="/login">Login</Link>
        </div>
    );
}
