import React from "react";
import { useLocation } from 'react-router-dom';
import {Link} from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
export default function Home() {
        const location = useLocation();
        const navigate= useNavigate();
        const user = location.state && location.state.user;
        const logout=()=>{
            localStorage.removeItem('token-info');
            window.location.replace('http://localhost:3000/login');
        }
        const upload=()=>{
            navigate('/upload',{ state: {user:user} });
        }
        const download=(event)=>{
            const pdfUrl = "http://localhost:9090/getdata"; // Replace with your PDF URL
  const iframe = document.createElement('iframe');
  iframe.src = pdfUrl;
  iframe.style.width = '50%';
  iframe.style.height = '500px'; // Set the desired height
  document.body.appendChild(iframe);
        }
        return (
            <div>
            <h1>Hello {user}</h1><br/>
            {user?(<div><button onClickCapture={logout}>Logout </button><button onClickCapture={upload}>Upload</button><button onClickCapture={download}>Download</button></div>):(<div><h1>Please Login to view options </h1><Link to='/login'>Login</Link></div>)}
            </div>
        );
}
