import React, { useEffect, useState } from "react";
import { useLocation } from 'react-router-dom';
import axios from 'axios';
export default function Job()
{
    const [companyname,setcompanyname]=useState("");
    const location = useLocation();
    const user = location.state && location.state.user;

    useEffect(
        ()=>{
            const [companyname,setcompanyname]=useState("");
        const location = useLocation();
    const user = location.state && location.state.user;
            axios.get('http://localhost:9090/getbyuser',user).then(
                response=>{
                    setcompanyname(response.data);
                }
            );
        },[]
    )

    const [jobname,setjobname]=useState("");
    const [jobdesc,setjobdesc]=useState("");
    const [jobloc,setjobloc]=useState("");
    const [jobtype,setjobtype]=useState("");
    const [jobsal,setjobsal]=useState("");
    const formdata=new FormData();
    formdata.append('jobname',jobname);
    formdata.append('jobdesc',jobdesc);
    formdata.append('jobloc',jobloc);
    formdata.append('jobtype',jobtype);
    formdata.append('jobsal',jobsal);
    formdata.append('companyname',companyname);

    const SaveVal=(event)=>
    {
        event.preventDefault();
        axios.post("http://localhost:9090/job",formdata).then(response => {
            const responseData = response.data;
            if(responseData==="Success")
            window.alert(responseData);
        });
    }

    return(
        <div>
        <h1>Hello Company Register for Job Role</h1>
        <form onSubmit={SaveVal}>
            Job Name : <input type="text" placeholder="Enter JobName" value={jobname} name="jobname" required onChange={(e)=>{setjobname(e.target.value)}}></input><br/>
            Job Description : <input type="text" placeholder="Enter JobDescription" value={jobdesc} name="jobdesc" required onChange={(e)=>{setjobdesc(e.target.value)}}></input><br/>
            Job Location : <input type="text" placeholder="Enter JobLocation" value={jobloc} name="jobloc" required onChange={(e)=>{setjobloc(e.target.value)}}></input><br/>
            Job Type : <input type="text" placeholder="Enter JobType" value={jobtype} name="jobtype" required onChange={(e)=>{setjobtype(e.target.value)}}></input><br/>
            Job Salary : <input type="text" placeholder="Enter JobSalary" value={jobsal} name="jobsal" required onChange={(e)=>{setjobsal(e.target.value)}}></input><br/>
            <button type="submit">Register</button>
        </form>
        </div>
    );
}
